sendFile() {
    if (this.dataChannel && this.file) {
    const chunkSize = 16384;  //  16KB
    const fileReader = new FileReader();

    let offset = 0;

    // 
    const fileInfo = {
        name: this.file.name,
        size: this.file.size,
    };

    this.dataChannel.send(JSON.stringify(fileInfo));  // 

    const sendNextChunk = () => {
        const fileSlice = this.file.slice(offset, offset + chunkSize);
        fileReader.onload = () => {
        this.dataChannel.send(fileReader.result);  // 
        offset += chunkSize;
        if (offset < this.file.size) {
            sendNextChunk();  // 
        } else {
            console.log('');
        }
        };
        fileReader.readAsArrayBuffer(fileSlice);  //  ArrayBuffer
    };

    sendNextChunk();  // 
    } else {
    console.error('');
    }
},
